import { Counter, Tab, CurrencyIcon } from '@ya.praktikum/react-developer-burger-ui-components'
import React from "react";
import { ingredientPropType } from "../../utils/prop-types";
import PropTypes from 'prop-types';
import ingredientsStyle from './burger-ingredients.module.css';

const BurgerIngredient = ({ _id, image, name, price }) => {
  return (
    <div className={ingredientsStyle.ingridient} key={_id}>
      <Counter count={1} size="default" extraClass="m-1" key={_id} />
      <img className={ingredientsStyle.ingridient__img} src={image} alt={name} />
      <div>
        <span className="text text_type_digits-default">{price + " "}</span>
        <CurrencyIcon type="primary" />
      </div>
      <p className="text text_type_main-default">{name}</p>
    </div>
  )
}

BurgerIngredient.propTypes = {
  _id: PropTypes.string.isRequired,
  image: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  price: PropTypes.number.isRequired
}

const BurgerIngredients = ({ data }) => {
  const [current, setCurrent] = React.useState('one');

  const handleClickTab = (value) => {
    setCurrent(value);
  };

  const renderIngredientsByType = (type) => {
    const filteredData = data.filter(item => item.type === type);
    return (
      <div className={ingredientsStyle.container}>
        {filteredData.map(item => <BurgerIngredient key={item._id} {...item} />)}
      </div>
    );
  };

  return (
    <section className={ingredientsStyle.section}>
      <h1 className="text text_type_main-large">Соберите бургер</h1>
      <div style={{ display: 'flex' }}>
        <Tab value="one" active={current === 'one'} onClick={() => handleClickTab('one')}>
          Булки
        </Tab>
        <Tab value="two" active={current === 'two'} onClick={() => handleClickTab('two')}>
          Соусы
        </Tab>
        <Tab value="three" active={current === 'three'} onClick={() => handleClickTab('three')}>
          Начинки
        </Tab>
      </div>
      <div className={ingredientsStyle.ingridients + " custom-scroll"}>
        <h2 className="text text_type_main-medium">Булки</h2>
        {renderIngredientsByType('bun')}
        <h2 className="text text_type_main-medium">Соусы</h2>
        {renderIngredientsByType('sauce')}
        <h2 className="text text_type_main-medium">Начинки</h2>
        {renderIngredientsByType('main')}
      </div>
    </section>
  )
}

BurgerIngredients.propTypes = {
  data: PropTypes.arrayOf(ingredientPropType).isRequired
}

export default BurgerIngredients;
