import React, { Component, useMemo } from "react";
import constructorStyles from './burger-constructor.module.css';
import { ConstructorElement, DragIcon, CurrencyIcon, Button } from '@ya.praktikum/react-developer-burger-ui-components';
import ingredientPropType from '../../utils/prop-types';
import PropTypes from 'prop-types';

const BurgerConstructor = (props) => {
  
  // const totalPrice = props.reduce((sum, item) => sum + item.price, 0);

  const bun = useMemo(() => {
    return props.data.find((item) => item.type === 'bun');
  }, [props]);
  const sauce = React.useMemo(() => {
    return props.data.find(item => item.type === typeSauce)
  }, [props]);
  const main = React.useMemo(() => {
    return props.data.filter(item => item.type === typeMain).slice(1, 5)
  }, [props]);
  
  const renderedBun = (item, type) => {
    return (
      <div key={item._id}>
        <ConstructorElement type={type} isLocked={true} text={item.name} price={item.price}
                            thumbnail={item.image} />
      </div>
    )
  }

  const renderedIngredient = (item, type) => {
    return (
      <div key={item._id}>
        <DragIcon type="primary" />
        <ConstructorElement text={type.name} price={type.price} thumbnail={type.image} />
      </div>
    )
  }

  return (
    <section className={constructorStyles.section}>
      <div className={" mt-25 pl-4"}>
      <ul>
        {bun && renderedBun(bun, "top")}
        <li className={" custom-scroll pr-4"}>
          <ul >
            {props && props.data.map((item, type) => {
              return renderedIngredient(item, type);
            })}
          </ul>
        </li>
        {bun && renderedBun(bun, "bottom")}
      </ul>
      <div className={" pr-8"}>
        <div>
          <p className="text text_type_digits-medium">610</p>
          <CurrencyIcon />
        </div>
        <Button htmlType="button" type="primary" size="large">
          Оформить заказ
        </Button>
      </div>
    </div>
    </section>
  )
}

BurgerConstructor.propTypes = {
  data: PropTypes.arrayOf(ingredientPropType).isRequired
}

export default BurgerConstructor;
